import React from "react";
import {
  StyleSheet,
  Dimensions,
  ScrollView,
  StatusBar,
  View
} from "react-native";
import { Block, Button, Icon, Text } from "galio-framework";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import Modal, { ModalContent } from "react-native-modals";
import { Ionicons } from "@expo/vector-icons";
import * as firebase from "firebase";

const { width, height } = Dimensions.get("screen");

class HomeScreen extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      visibleServe: false,
      visibleJanTV: false
    };
  }

  render() {
    return (
      <ScrollView
        showsVerticalScrollIndicator={false}
        contentContainerStyle={{ paddingBottom: 0 }}
      >
        <StatusBar hidden={false}></StatusBar>
        <Block>
          <Block flex>
            <Block>
              <Text
                color="#4f3961"
                size={25}
                style={{ marginTop: 10, marginLeft: 15 }}
              >
                Choose Category
              </Text>
              <Text color="#4f3961" size={18} style={{ marginLeft: 15 }}>
                Choose category to proceed
              </Text>
            </Block>
            <Block flex>
              <Block center>
                <Button
                  color="rgba(176, 108, 168, 1)"
                  style={styles.button}
                  onPress={() => this.props.navigation.navigate("Suggest")}
                >
                  <Block row>
                    <Icon
                      name="ios-help"
                      family="Ionicon"
                      size={50}
                      color={"white"}
                      style={{ marginRight: 5 }}
                    />
                    <Text style={styles.socialTextButtons}>
                      Bring To Attention
                    </Text>
                  </Block>
                </Button>
              </Block>
              <Block center>
                <Button
                  color="rgba(49, 176, 170, 1)"
                  style={styles.button}
                  onPress={() => {
                    this.setState({ visibleServe: true, visibleJanTV: false });
                  }}
                >
                  <Block row>
                    <Icon
                      name="ios-send"
                      family="Ionicon"
                      size={30}
                      color={"white"}
                      style={{ marginRight: 5 }}
                    />
                    <Text style={styles.socialTextButtons}>
                      How Can I Serve
                    </Text>
                  </Block>
                </Button>
              </Block>
              <Modal
                visible={this.state.visibleServe}
                onTouchOutside={() => {
                  this.setState({ visibleServe: false, visibleJanTV: false });
                }}
              >
                <ModalContent>
                  <Button
                    color="rgba(49, 176, 170, 1)"
                    style={styles.buttonPopup}
                    onPress={() => {
                      this.setState({
                        visibleServe: false,
                        visibleJanTV: false
                      });
                      this.props.navigation.navigate("Medical");
                    }}
                  >
                    <Block row>
                      <Icon
                        name="ios-medkit"
                        family="Ionicon"
                        size={30}
                        color={"white"}
                        style={{ marginRight: 5 }}
                      />
