import React from "react";
import {
  Image,
  StyleSheet,
  StatusBar,
  Dimensions,
  TouchableOpacity,
  Alert,
  CheckBox,
  View,
  KeyboardAvoidingView
} from "react-native";
import { Block, Button, Text, theme, Input, Icon } from "galio-framework";
import ApiKeys from "../constants/ApiKeys";
import * as firebase from "firebase";
import Spinner from "react-native-loading-spinner-overlay";

const { width } = Dimensions.get("screen");

class Onboarding extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      spinner: false,
      name: "",
      email: "",
      age: "",
      place: "",
      mobile: "",
      password: "",
      loginBtnText: "LOG IN",
      signUpBtnText: "Sign Up",
      formLogin: 1,
      formSignup: 0,
      formEnterOTP: 0,
      formForgotPwd: 0,
      loginFlag: 0,
      terms: false,
    };

    if (!firebase.apps.length) {
      firebase.initializeApp(ApiKeys.FirebaseConfig);
    }
  }

  updateCheckedState = () => {
    this.setState({ terms: !this.state.terms });
  };

  updateState = async () => {
    this.setState({ spinner: true });
  };

  loginBtnPressed = () => {
    this.updateState();
    const { navigation } = this.props;

    if (this.state.loginBtnText === "LOG IN") {
      if (this.state.email === "" || this.state.password === "") {
        this.setState({ spinner: false });
        alert("Fill all fields");
        return;
      }
      firebase
        .auth()
        .signInWithEmailAndPassword(this.state.email, this.state.password)
        .then((user) => {
          global.User = user;
          this.setState({ spinner: false });
          navigation.navigate("App");
        })
        .catch((error) => {
          this.setState({ spinner: false });
          alert(error.message);
        });
    } else if (this.state.loginBtnText === "SIGN UP") {
      if (
        this.state.name === "" ||
        this.state.email === "" ||
        this.state.age === "" ||
        this.state.place === "" ||
        this.state.mobile === "" ||
        this.state.password === "" ||
        this.state.terms === false
      ) {
        this.setState({ spinner: false });
        alert("Fill all fields");
        return;
      }
      firebase
        .auth()
        .createUserWithEmailAndPassword(this.state.email, this.state.password)
        .then((userCredentials) => {
          firebase
            .database()
            .ref("users/" + userCredentials.user.uid + "/profile/")
            .set({
              name: this.state.name,
              email: this.state.email,
              age: this.state.age,
              place: this.state.place,
              mobile: this.state.mobile,
            })
            .then(() => {
              alert("Registration Complete!\nPlease Login");
              this.setState({
                formLogin: 1,
                formSignup: 0,
                formEnterOTP: 0,
                formForgotPwd: 0,
                loginFlag: 0,
                loginBtnText: "LOG IN",
                signUpBtnText: "Sign Up",
              });
            })
            .catch((error) => {
              this.setState({ spinner: false });
              alert(error.message);
            });
        })
        .catch((error) => {
          this.setState({ spinner: false });
          alert(error.message);
        });
    } else if (this.state.loginBtnText === "RESET PASSWORD") {
      if (this.state.email === "") {
        this.setState({ spinner: false });
        alert("Enter email id");
        return;
      }
      firebase
        .auth()
        .sendPasswordResetEmail(this.state.email)
        .then((user) => {
          this.setState({ spinner: false });
          alert("Please check your email");
        })
        .catch((error) => {
          this.setState({ spinner: false });
          alert(error);
        });
    }
  };

  signupBtnPressed() {
    if (this.state.formSignup === 0) {
      this.setState({
        formLogin: 0,
        formSignup: 1,
        formEnterOTP: 0,
        formForgotPwd: 0,
        loginBtnText: "SIGN UP",
        signUpBtnText: "Log In",
      });
    } else {
      this.setState({
        formLogin: 1,
        formSignup: 0,
        formEnterOTP: 0,
        formForgotPwd: 0,
        loginBtnText: "LOG IN",
        signUpBtnText: "Sign Up",
      });
    }
  }

  forgotBtnPressed() {
    this.setState({
      formLogin: 0,
      formSignup: 0,
      formEnterOTP: 0,
      formForgotPwd: 1,
      loginBtnText: "RESET PASSWORD",
      signUpBtnText: "Sign Up",
    });
  }

  phoneOTPForm() {
    this.setState({
      formLogin: 0,
      formSignup: 0,
      formEnterOTP: 1,
