import React from "react";
import {
  StyleSheet,
  ScrollView,
  StatusBar,
  Platform,
  Dimensions,
} from "react-native";
import { Block, Button, Text, Icon } from "galio-framework";
import Modal, { ModalContent } from "react-native-modals";

import * as firebase from "firebase";
import * as Permissions from "expo-permissions";
import { Notifications } from "expo";
import { refresh } from "../../constants/Data";
const { width, height } = Dimensions.get("screen");
import Constants from "expo-constants";

class DashboardScreen extends React.Component {
  state = {
    visibleServe: false,
    visibleJanTV: false,
  };

  constructor(props) {
    super(props);

    this.registerForPushNotificationsAsync(global.User);

    refresh();

    firebase
      .database()
      .ref("users/" + global.User.user.uid + "/profile/")
      .once("value")
      .then((snapshot) => {
        var name = snapshot.val()["name"];
        global.currentUserName = name;
      });
  }

  registerForPushNotificationsAsync = async (currentUser) => {
    if (Constants.isDevice) {
      const { status: existingStatus } = await Permissions.getAsync(
        Permissions.NOTIFICATIONS
      );
      let finalStatus = existingStatus;
      if (existingStatus !== "granted") {
        const { status } = await Permissions.askAsync(
          Permissions.NOTIFICATIONS
        );
        finalStatus = status;
      }
      if (finalStatus !== "granted") {
        alert("Failed to get push token for push notification!");
        return;
      }

      let token = await Notifications.getExpoPushTokenAsync();

      var updates = {};
      updates["/expoToken"] = token;
      await firebase
        .database()
        .ref("users/" + currentUser.user.uid)
        .update(updates);
    } else {
      alert("Must use physical device for Push Notifications");
    }

    if (Platform.OS === "android") {
      Notifications.createChannelAndroidAsync("default", {
        name: "default",
        sound: true,
        priority: "max",
        vibrate: [0, 250, 250, 250],
      });
    }
  };

  render() {
    return (
      <ScrollView
        showsVerticalScrollIndicator={false}
        contentContainerStyle={{ paddingBottom: 0 }}
      >
        <StatusBar hidden={false}></StatusBar>
        <Block style={styles.container}>
          <Block flex>
            <Block>
              <Text
                color="#4f3961"
                size={25}
                style={{ marginTop: 10, marginLeft: 15 }}
              >
                Choose Category
              </Text>
              <Text color="#4f3961" size={18} style={{ marginLeft: 15 }}>
                Choose category to proceed
              </Text>
            </Block>
            <Block flex>
              <Block center>
                <Button
                  color="rgba(176, 108, 168, 1)"
                  style={styles.button}
                  onPress={() =>
                    this.props.navigation.navigate("Suggest", {
                      handleData: this.handleData,
                    })
                  }
                >
                  <Block row>
                    <Icon
                      name="ios-help"
                      family="Ionicon"
                      size={50}
                      color={"white"}
                      style={{ marginRight: 5 }}
                    />
                    <Text style={styles.socialTextButtons}>
                      Bring To Attention
                    </Text>
                  </Block>
                </Button>
              </Block>
              <Block center>
                <Button
                  color="rgba(49, 176, 170, 1)"
